import { Button } from '@material-ui/core'
import AddIcon from '@material-ui/icons/Add';

import Header from '../store/container/header'
import { checkAuth } from '../utils/auth'

const DashboardBody = () => {
  return (
    <div style={{ display: 'flex', justifyContent: 'center', marginTop: 50, flexWrap: 'wrap', width: '500px', margin: 'auto' }}>
      <div style={{ width: 500, display: 'flex', justifyContent: 'center' }}>
        {/* Display all announces */}
        <h3>No announces yet</h3>
        <Button variant="contained">
          Create new announce
          <AddIcon />
        </Button>
      </div>

      <div>
        DB body
      </div>
    </div>
  );
};

const Dashboard = ({ isLoggedIn }) => {
  return (
    <div>
      <Header isLoggedIn={isLoggedIn} />
      <DashboardBody />
    </div>
  );
};

Dashboard.getInitialProps = async (ctx) => {
  const isLoggedIn = await checkAuth(ctx);
  //.. get current announces
  return {
    isLoggedIn,
    namespacesRequired: ['common']
  };
}

export default Dashboard;
