import React, { useState } from 'react'

import { Button, FormControl, InputLabel, Input } from '@material-ui/core'
import AddIcon from '@material-ui/icons/Add';
import ClearIcon from '@material-ui/icons/Clear';

import Header from '../store/container/header'
import { checkAuth } from '../utils/auth'

const DashboardBody = () => {
  const [ announceToolStatus, setAnnounceToolStatus ] = useState(false)

  return (
    <div style={{ display: 'flex', justifyContent: 'center', flexWrap: 'wrap', width: '500px', margin: 'auto' }}>
      <div style={{ width: 500, display: 'flex', justifyContent: 'space-between', marginTop: 50 }}>
        <h3>No announces yet</h3>
        <Button
          variant='contained'
          style={{ backgroundColor: announceToolStatus ? '#ff4d4d' : '#4ecca3' }}
          onClick={() => setAnnounceToolStatus(!announceToolStatus)}
        >
          Create new announce
          { !announceToolStatus ? <AddIcon /> : <ClearIcon />}
        </Button>
      </div>

      {
        announceToolStatus ?
        <div>
          <FormControl>
            <InputLabel htmlFor='title'>title</InputLabel>
            <Input type='text' name='title' variant='' />
          </FormControl>  
        </div> :
        null
      }
    </div>
  );
};

const Dashboard = ({ isLoggedIn }) => {
  return (
    <div>
      <Header isLoggedIn={isLoggedIn} />
      <DashboardBody />
    </div>
  );
};

Dashboard.getInitialProps = async (ctx) => {
  const isLoggedIn = await checkAuth(ctx);
  //.. get current announces
  return {
    isLoggedIn,
    namespacesRequired: ['common']
  };
}

export default Dashboard;
